import React, { Component } from 'react';
import './App.css';

export class HappyBirthday extends Component {

  constructor(props) {
    super(props);
    this.state = {
      bannerClass: 'banner-hide'
    }
  }
  componentDidMount() {
    const date = new Date();
    const getDay = date.getDate();
    if (getDay >= 24) {
      this.setTimeout(() => {
        this.showBanner();
      }, 5000);
    }
  }

  showBanner = () => {
    this.setState({
      bannerClass: 'banner-show'
    })
  }
  render() {
    return (
      <React.Fragment>
        <div>Clock</div>
      <div className="App">
        <header className={`App-header ${this.state.bannerClass}`}>
          Happy Birthday Senorita
        </header>
      </div>
      </React.Fragment>
    );
  }
}
